mapper.select:
SELECT A.*, B.MEMBER_NAME FROM apply A, member B WHERE A.MEMBER_NO = B.MEMBER_NO AND APPLY_FG = 1 AND DELETE_YN = 'N'
AND A.APPLY_CATE = #{searchSelect2}
AND B.MEMBER_NAME LIKE CONCAT('%', #{searchContent}, '%')
AND A.APPLY_CONTENT LIKE CONCAT('%', #{searchContent}, '%')
ORDER BY APPLY_NO DESC LIMIT #{startRow}, #{endRow}

mapper.update:
UPDATE apply
SET DELETE_YN = 'Y'
WHERE APPLY_NO = #{apply_no}

UPDATE apply
SET DELETE_YN = 'Y'
WHERE APPLY_NO = #{apply_no}

UPDATE board
SET DELETE_YN = 'Y'
WHERE BOARD_NO = #{board_no}

UPDATE board
SET BOARD_TITLE = #{BOARD_TITLE}
	, BOARD_CONTENT = #{BOARD_CONTENT}
	, RECENT_UPDATE_DATE = NOW()
WHERE BOARD_NO = ${BOARD_NO}

UPDATE board
SET DELETE_YN = 'Y'
WHERE BOARD_NO = #{board_no}

UPDATE board
SET BOARD_TITLE = #{BOARD_TITLE}
	, BOARD_CONTENT = #{BOARD_CONTENT}
	, RECENT_UPDATE_DATE = NOW()
WHERE BOARD_NO = ${BOARD_NO}
