mapper.select:
SELECT * 
FROM member
WHERE MEMBER_ID = #{MEMBER_ID} AND MEMBER_PWD = #{MEMBER_PWD}

SELECT COUNT(*)
FROM member
WHERE MEMBER_ID = #{member_id}

mapper.insert:
	INSERT INTO member(   
      MEMBER_ID
    , MEMBER_PWD
    , MEMBER_NAME
    , MEMBER_PHONE
    , MEMBER_EMAIL
    , MEMBER_POSTCODE
    , MEMBER_ADDR
    , MEMBER_ADDR_DETAIL
    , MEMBER_ADDR_EXTRA
    , MEMBER_DETAIL
    )
    VALUES(
      #{MEMBER_ID}
    , #{MEMBER_PWD}
    , #{MEMBER_NAME}
    , #{MEMBER_PHONE}
    , #{MEMBER_EMAIL}
    , #{MEMBER_POSTCODE}
    , #{MEMBER_ADDR}
    , #{MEMBER_ADDR_DETAIL}
    , #{MEMBER_ADDR_EXTRA}
    , ""
    )

mapper.select:
SELECT MEMBER_ID
FROM member
WHERE MEMBER_EMAIL = #{MEMBER_EMAIL}
AND MEMBER_NAME = #{MEMBER_NAME}

SELECT COUNT(*)
FROM member
WHERE MEMBER_ID = #{MEMBER_ID}
AND MEMBER_EMAIL = #{MEMBER_EMAIL}

mapper.update:
UPDATE member
SET MEMBER_PWD = #{MEMBER_PWD}
WHERE MEMBER_ID = #{MEMBER_ID}
